import React, { Component } from 'react'
import { withAuth0 } from '@auth0/auth0-react';
import axios from 'axios';
import { Col ,Row ,Card ,Button , Modal} from 'react-bootstrap';
export class Favorite extends Component {

    constructor() {
        super();
        this.state = {
          allJuice: [],
          showModal:false,
        };
      }
      componentDidMount() {
        axios.get(`https://juicepine.herokuapp.com/favorite/${this.props.auth0.user.email}`).then((response) => {
          console.log(response.data);
          this.setState({
            allJuice: response.data,
          });
        });
      }
      componentDidUpdate() {
        axios.get(`https://juicepine.herokuapp.com/favorite/${this.props.auth0.user.email}`).then((response) => {
          this.setState({
            allJuice: response.data,
          });
        });
      }
       deleteFav(id){

        axios.delete(`https://juicepine.herokuapp.com/delete-fav/${id}`).then((response) => {
            console.log(response.data);

          });
       }


       updateFav(){
          this.setState({
              showModal:true,
          })

       }

    render() {
        return (<> 
                  <Modal show={this.state.showModal} onHide={this.props.hidden}>
            <Modal.Header closeButton>
              <Modal.Title>Modal heading</Modal.Title>
            </Modal.Header>
            <Modal.Body> <Form onSubmit={this.props.post}>
                        <Form.Control type="text" placeholder="Title of Book" name="userTitle" />
                        <Form.Control type="text" placeholder="Description of Book" name="userDes"/>
                        <Form.Control type="text" placeholder="Status of Book" name="userStat"/>
                        <Button type="submit">Submit</Button>
                         </Form></Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.props.hidden}>
                Close
              </Button>
            </Modal.Footer>
            </Modal>

            <Row>
            {this.state.allJuice.map((item,ind) => {
              return (
               <Col lg={4} key={ind}> <Card style={{ width: "18rem" }}>
               <Card.Img variant="top" src={item.juiceUrl} />
               <Card.Body>
                 <Card.Title>{item.juiceName}</Card.Title>
                 <Card.Title>{item.juiceDes} </Card.Title>
                 <Button variant="danger" onClick={()=>this.deleteFav(item._id)} >Delete</Button> 
                 <Button variant="info" onClick={()=>this.updateFav(item._id)} >Update</Button>
               </Card.Body>
             </Card></Col>
              );
            })}
          </Row>
          </>
        )
    }
}

export default withAuth0(Favorite);
