import React, { Component } from "react";
import axios from "axios";
import { Card ,Button,Row,Col ,Alert} from "react-bootstrap";
import { withAuth0 } from "@auth0/auth0-react";
import Swal from 'sweetalert2';
class Juice extends Component {
  constructor() {
    super();
    this.state = {
      allJuice: [],
      userName:""
    };
  }
  componentDidMount() {
    axios.get(`https://juicepine.herokuapp.com/juice`).then((response) => {
      this.props.auth0.isAuthenticated && this.setState({
        userName:this.props.auth0.user.name,
      }) 
      this.setState({
        allJuice: response.data,
      });
    });
  }
  addFavorite(item){
      let bodyJuice={
        email:this.props.auth0.user.email,
        name:item.name,
        url:item.image,
        des:item.description
      }
      axios.post(`https://juicepine.herokuapp.com/add-fav`,bodyJuice).then(response=>{
          console.log(response.data);

          Swal.fire('Added To Favorite Successfully')
      })
  }

  render() {
    return (
      <Row>
        {this.props.auth0.isAuthenticated ? <Alert style={{textAlign:"center"}}  variant="success">
              Welcome {this.props.auth0.user.name}
  </Alert> : <Alert style={{textAlign:"center"}}  variant="success">
              Good Bye {this.props.auth0.user.name}
  </Alert>  }
        {this.state.allJuice.map((item,ind) => {
          return (
           <Col lg={4} key={ind}> <Card style={{ width: "18rem" }}>
           <Card.Img variant="top" src={item.image} />
           <Card.Body>
             <Card.Title>{item.name}</Card.Title>
             <Card.Title>{item.description} </Card.Title>
             {this.props.auth0.isAuthenticated &&<Button variant="primary" onClick={()=>this.addFavorite(item)}>Add To Favorite</Button> }
           </Card.Body>
         </Card></Col>
          );
        })}
      </Row>
    );
  }
}

export default withAuth0(Juice);
