import React, { Component } from 'react'
import { withAuth0 } from '@auth0/auth0-react';
import axios from 'axios';
import { Col ,Row ,Card ,Button } from 'react-bootstrap';
export class Favorite extends Component {

    constructor() {
        super();
        this.state = {
          allJuice: [],
        };
      }
      componentDidMount() {
        axios.get(`https://juicepine.herokuapp.com/favorite/${this.props.auth0.user.email}`).then((response) => {
          console.log(response.data);
          this.setState({
            allJuice: response.data,
          });
        });
      }
       deleteFav(newId){
           let body={
               id:newId
           }
        axios.get(`https://juicepine.herokuapp.com/delete-fav`,body).then((response) => {
            console.log(response.data);

          });
       }

    render() {
        return (
            <Row>
            {this.state.allJuice.map((item,ind) => {
              return (
               <Col lg={4} key={ind}> <Card style={{ width: "18rem" }}>
               <Card.Img variant="top" src={item.juiceUrl} />
               <Card.Body>
                 <Card.Title>{item.juiceName}</Card.Title>
                 <Card.Title>{item.juiceDes} </Card.Title>
                 <Button variant="danger" onClick={()=>this.deleteFav(item._id)} >Delete</Button> 
               </Card.Body>
             </Card></Col>
              );
            })}
          </Row>
        )
    }
}

export default withAuth0(Favorite);
