import React, { Component } from "react";
import { withAuth0 } from "@auth0/auth0-react";
import axios from "axios";
import { Col, Row, Card, Button, Modal, Form } from "react-bootstrap";
export class Favorite extends Component {
  constructor() {
    super();
    this.state = {
      allJuice: [],
      showModal: false,
    };
  }
  componentDidMount() {
    axios
      .get(
        `https://juicepine.herokuapp.com/favorite/${this.props.auth0.user.email}`
      )
      .then((response) => {
        console.log(response.data);
        this.setState({
          allJuice: response.data,
        });
      });
  }
  componentDidUpdate() {
    axios
      .get(
        `https://juicepine.herokuapp.com/favorite/${this.props.auth0.user.email}`
      )
      .then((response) => {
        this.setState({
          allJuice: response.data,
        });
      });
  }
  deleteFav(id) {
    axios
      .delete(`https://juicepine.herokuapp.com/delete-fav/${id}`)
      .then((response) => {
        console.log(response.data);
      });
  }

  showUpdate() {
    this.setState({
      showModal: true,
    });
  }

  setShow(a){
    this.setState({
        showModal: a,
      });

  }
  updateJuice(e ,id){
   e.preventDefault();
   let bodyJuice={
    name:e.target.name.nameJ,
    url:e.target.name.imgUrl,
    des:e.target.name.des
  }
   axios
   .delete(`https://juicepine.herokuapp.com/update-fav/${id}` , bodyJuice)
   .then((response) => {
     console.log(response.data);
   });

  }





  render() {
    return (
      <>
        <Modal show={this.state.showModal} onHide={() => setShow(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {" "}
            <Form onSubmit={()=>this.updateJuice(e)}>
              <Form.Control
                type="text"
                placeholder="Name of Your Juice"
                name="nameJ"
              />
              <Form.Control
                type="text"
                placeholder="Image fo Your Juice"
                name="imgUrl"
              />
              <Form.Control type="text" placeholder="Descriptions" name="des" />
              <Button type="submit">Submit</Button>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.state.showModal}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>

        <Row>
          {this.state.allJuice.map((item, ind) => {
            return (
              <Col lg={4} key={ind}>
                {" "}
                <Card style={{ width: "18rem" }}>
                  <Card.Img variant="top" src={item.juiceUrl} />
                  <Card.Body>
                    <Card.Title>{item.juiceName}</Card.Title>
                    <Card.Title>{item.juiceDes} </Card.Title>
                    <Button
                      variant="danger"
                      onClick={() => this.deleteFav(item._id)}
                    >
                      Delete
                    </Button>
                    <Button
                      variant="info"
                      style={{ marginLeft: "10px" }}
                      onClick={() => this.showUpdate(e,item._id)}
                    >
                      Update
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            );
          })}
        </Row>
      </>
    );
  }
}

export default withAuth0(Favorite);
